import 'package:flutter/material.dart';

/* -------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------- */
/* "Provided comprehensive details about implementing Container widget in Flutter, including customization using BoxDecoration, adding borders, shadows, and understanding key properties like constraints, padding, and margin. Ready for implementation!"                                                                                                 */
/* -------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------- */
class ContainerWidget extends StatelessWidget {
  const ContainerWidget({super.key});

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(title: const Text('Container Widget')),
      body: Container(
        /* -------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------- */
        /* Padding refers to the space or margin around the content of a widget, helping to create distance between the content and the widget's edges.                                                                                                                                                                                                             */
        /* -------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------- */

        // padding: EdgeInsets.zero, // To remove padding from the container
        // padding: EdgeInsets.all(20), // To add padding from all side
        // padding: EdgeInsets.only(top: 10, left: 20, right: 0, bottom: 0),
        padding: const EdgeInsets.symmetric(horizontal: 20.0, vertical: 30.0),

        /* -------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------- */
        /* Margin refers to the space outside the boundaries of a widget, providing separation between the widget and its surrounding elements or container.                                                                                                                                                                                                        */
        /* -------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------- */

        // margin: EdgeInsets.zero, // To remove margin from the container
        // margin: EdgeInsets.all(20), // To add margin from all side
        // margin: EdgeInsets.only(top: 10, left: 20, right: 0, bottom: 0), // To add margin from specific side
        margin: const EdgeInsets.symmetric(horizontal: 20.0, vertical: 30.0),

        /* -------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------- */
        /* This code sets dynamic height constraints for a Container widget.                                                                                                                                                                                                                                                                                        */
        /* -------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------- */
        constraints: BoxConstraints.expand(
          height: Theme.of(context).textTheme.headlineMedium!.fontSize! * 1.1 +
              200.0,
        ),

        /* -------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------- */
        /* The transform applies a slight rotation to the entire contraption to complete the effect.                                                                                                                                                                                                                                                                */
        /* -------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------- */
        transform: Matrix4.rotationZ(0.1),

        // alignment: Alignment.center, // To align 'Inner Text' in the center
        // height: 200, // To set height in vertical dimension
        // width: 200, // To set width in horizontal dimension

        // color: Colors.amber, // remove color from here if you are using decoration and use this saperately in decoration like below
        decoration: BoxDecoration(
            color: Colors.amber,
            // borderRadius: BorderRadius.circular(10), // To add circular border radius from all side

            /* -------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------- */
            /* To add circular border radius from specific side                                                                                                                                                                                                                                                                                                         */
            /* -------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------- */

            borderRadius: const BorderRadius.only(
              topLeft: Radius.circular(10),
              bottomRight: Radius.circular(
                50,
              ),
            ),

            /* -------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------- */
            /* To add all side border                                                                                                                                                                                                                                                                                                                                   */
            /* -------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------- */

            // border: Border.all(
            //   color: Colors.brown,
            //   width: 5,
            //   strokeAlign: 3.0,
            //   style: BorderStyle.solid,
            // ),

            /* -------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------- */
            /* To add shadow to the box                                                                                                                                                                                                                                                                                                                                 */
            /* -------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------- */
            boxShadow: [
              BoxShadow(
                color: Colors.grey.withOpacity(0.5),
                spreadRadius: 3,
                blurRadius: 10,
                offset: const Offset(0, 3), // changes the shadow position
                blurStyle: BlurStyle.inner,
              )
            ]),

        child: Text(
          'Hello World',
          /* -------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------- */
          /* This code customizes the Text widget by applying a predefined medium headline style from the current theme and setting its color to white.                                                                                                                                                                                                               */
          /* -------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------- */
          style: Theme.of(context)
              .textTheme
              .headlineMedium!
              .copyWith(color: Colors.white),
        ),
      ),
    );
  }
}
